contracts/GameFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
// import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Types} from "./Types.sol"; // 引入公共类型库
import {GameInstance} from "./GameInstance.sol";
import {UserLevelManager} from "./UserLevelManager.sol";


contract GameFactory is AccessControl {

    // immutable
    address immutable BLZ_TOKEN_ADDRESS;
    address immutable LEVEL_MANAGER_ADDRESS;

    address[] public allGames;   // 所有比赛实例

    mapping(address => address[]) public gamesByCreator; // 某人创建的比赛

    event GameCreated(address indexed game, address indexed creator);
    event FeesWithdrawn(address indexed token, address indexed recipient, uint amount);

    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");

    mapping(address => uint) public feeBalances; // 管理多代币费用的映射：代币地址 => 累积的费用金额


    // 构造函数：设置初始 Owner
    constructor(address blitzTokenAddress, address levelManagerAddress) {
        BLZ_TOKEN_ADDRESS = blitzTokenAddress;
        LEVEL_MANAGER_ADDRESS = levelManagerAddress;
        // 设置合约部署者为 Owner 和 DEFAULT_ADMIN_ROLE
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OWNER_ROLE, msg.sender);
        _setRoleAdmin(OWNER_ROLE, DEFAULT_ADMIN_ROLE);
    }


    function createGame(Types.GameConfig memory config) external returns (address gameInstance) {
        // 1. 计算费用和总转账金额
        uint totalPrizePool = config.prizePool;
        // 5% 费用计算 (10000 基点中的 500)
        uint creatorFee = (totalPrizePool * 500) / 10000; 
        uint totalTransferAmount = totalPrizePool + creatorFee;

        // 2. 检查创建者是否已预授权 GameFactory 支付总金额
        require(
            IERC20(config.prizeTokenAddress).allowance(msg.sender, address(this)) >= totalTransferAmount,
            "Factory not approved for prize/fee token transfer"
        );

        // 3. 将总金额 (奖池 + 费用) 从创建者转入 Factory 合约
        // 注意：这里的 address(this) 是 GameFactory 合约地址
        require(IERC20(config.prizeTokenAddress).transferFrom(
            msg.sender, 
            address(this), 
            totalTransferAmount
        ), "Transfer failed");

        // 4. 部署新比赛实例 (全量部署模式)
        GameInstance newGame = new GameInstance();
        gameInstance = address(newGame);

        // 5. 初始化比赛实例
        config.feeTokenAddress = BLZ_TOKEN_ADDRESS;
        newGame.initialize(config, msg.sender, LEVEL_MANAGER_ADDRESS);

        // 6. 将初始奖池金额转入新部署的 GameInstance 合约
        // 费用 (creatorFee) 留在 Factory 合约中，等待 Factory Owner 提取。
        if (totalPrizePool > 0) {
            // Factory 将奖池部分转给新的 GameInstance 合约
            require(IERC20(config.prizeTokenAddress).transfer(gameInstance, totalPrizePool), "Transfer failed");
        }


        // 7. 记录
        if (creatorFee > 0) {
            feeBalances[config.prizeTokenAddress] += creatorFee; // <--- 关键：记录费用
        }
        allGames.push(gameInstance);
        gamesByCreator[msg.sender].push(gameInstance);

        // 给创建者增加经验和解锁成就（通过 UserLevelManager）
        UserLevelManager levelManager = UserLevelManager(LEVEL_MANAGER_ADDRESS);
        // 创建比赛奖励：5 BLZ 代币 = 5 经验
        levelManager.addExp(msg.sender, 5 * 10**18);

        emit GameCreated(gameInstance, msg.sender);

    }

    /// @notice 只有 Owner 才能调用，提取 Factory 合约中所有累积的代币费用
    function withdrawFees(address tokenAddress) external onlyRole(OWNER_ROLE) { // <--- 新增
        uint amount = feeBalances[tokenAddress];
        require(amount > 0, "No fees to withdraw for this token");

        // 1. 清零费用记录（重要：防止重入攻击）
        feeBalances[tokenAddress] = 0;

        // 2. 执行代币转账
        // 从 Factory 合约转账给调用者 (Owner)
        require(IERC20(tokenAddress).transfer(msg.sender, amount), "Transfer failed"); 

        emit FeesWithdrawn(tokenAddress, msg.sender, amount);
    }



    /// @notice 返回系统中所有比赛
    function getAllGames() external view returns (address[] memory) {
        return allGames;
    }

    /// @notice 返回系统中部分比赛
    /// @param begin 起始索引 (基于 0)
    /// @param count 要返回的比赛数量
    function getPartofGames(uint begin, uint count) 
        external 
        view 
        returns (address[] memory) 
    {
        // 1. 检查起始索引是否越界
        require(begin < allGames.length, "Invalid start index (out of bounds)");

        // 2. 计算实际的结束索引
        // 结束索引应该是 min(begin + count, allGames.length)
        uint end = begin + count;

        // 如果 end 超过了数组总长度，就设置为总长度
        if (end > allGames.length) {
            end = allGames.length;
        }

        // 3. 计算要返回的实际数量
        uint actualCount = end - begin;

        // 4. 创建结果数组并复制数据
        address[] memory result = new address[](actualCount);

        for (uint i = 0; i < actualCount; i++) {
            // 从 allGames[begin] 开始复制
            result[i] = allGames[begin + i];
        }

        return result;
    }

    /// @notice 返回比赛总数，方便链下应用计算分页总页数
    function getTotalGames() external view returns (uint) {
        return allGames.length;
    }


}

contracts/GameInstance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
// import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Types} from "./Types.sol"; // 引入公共类型库
import {GameRegistry} from "./GameRegistry.sol";
import {UserLevelManager} from "./UserLevelManager.sol";

/// @title GameInstance (合并版 V3)
contract GameInstance is AccessControl {
    // using Types for *; 

    // AccessControl 的管理员角色哈希
    bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");
    bool private initialized;

    // Game Registry 和 Level Manager 地址
    address public gameRegistry;
    address public levelManager;

    // ====== 比赛基础数据（由 initialize 设置） ======
    address public creator;
    string public title;
    string public description;
    Types.GameType public gameType; // 新增：游戏类型

    uint public minPlayers;
    uint public maxPlayers;

    uint public registrationEndTime;
    uint public gameStartTime;

    address public feeToken;
    uint public entryFee;
    mapping(address => uint) playerEntryFees;

    address public prizeToken;
    uint public prizePool;
    Types.PrizeDistributionType public distributionType;
    uint[] public rankPrizes; //索引作为排名
    mapping(address => uint) public prizeToClaimsAmount; // 玩家地址 => 可领取的奖金数量


    // ====== 比赛状态数据 ======
    Types.GameStatus public status;                  
    Types.PlayerInfo[] public players;
    mapping(address => bool) public isJoined;              
    mapping(address => uint) internal scores;
    mapping(address => Types.GameResult) public gameResults; // 新增：存储玩家的游戏结果                      
    address[] public winners;


    // ====== 事件 ======
    event Initialized(address creator, string title);
    event PlayerJoined(address player);
    event ScoreSubmitted(address player, uint score);
    event WinnersSet(address[] winner);
    event PrizeDistributed(address winner, uint amount);
    event FeeRefund(address player, uint amount);
    event GameStarted();
    event GameCanceled(address indexed creator, uint refundAmount);
    event PlayerUnregistered(address indexed player, uint feeRefunded);

    // ====== 修饰符 ======


    /// @notice 初始化函数
    function initialize(Types.GameConfig memory config, address _creator, address _levelManager) external {
        // ... (基础检查保持不变)
        require(!initialized, "Already initialized");
        initialized = true;

        // 设置 levelManager 地址
        levelManager = _levelManager;

        // 时间检查
        require(config.registrationEndTime > block.timestamp, "Reg end must be in future");
        require(config.gameStartTime >= config.registrationEndTime, "Game must start after reg ends");
        // 人数检查
        require(config.maxPlayers >= config.minPlayers, "Max must be >= Min");

        // 设置所有基础信息
        creator = _creator;
        title = config.title;
        description = config.description;
        status = Types.GameStatus.Created;
        gameType = config.gameType; // 新增：设置游戏类型

        feeToken = config.feeTokenAddress;
        entryFee = config.entryFee;

        minPlayers = config.minPlayers;
        maxPlayers = config.maxPlayers;

        registrationEndTime = config.registrationEndTime;
        gameStartTime = config.gameStartTime;

        prizeToken = config.prizeTokenAddress;
        prizePool = config.prizePool;
        distributionType = config.distributionType;
        rankPrizes = config.rankPrizes;

        // 自定义排名配置校验
        if (config.distributionType == Types.PrizeDistributionType.CustomRanked) {
            uint rankPrizesLength = config.rankPrizes.length;
            // 1. 长度检查：至少配置一个排名
            require(rankPrizes.length > 0, "Custom rank config format error");

            // 2. 总和检查：计算所有 BPS 总和
            uint totalBps = 0;
            for (uint i = 0; i < rankPrizesLength; i += 1) {
                totalBps += rankPrizes[i];
            }
            require(totalBps <= 10000, "Prize BPS exceeds 100%"); // 
        }

        // 设置权限 (使用 OpenZeppelin v5.x 标准的 _grantRole)
        _grantRole(DEFAULT_ADMIN_ROLE, _creator);
        _grantRole(CREATOR_ROLE, _creator);
        _setRoleAdmin(CREATOR_ROLE, DEFAULT_ADMIN_ROLE);

        emit Initialized(_creator, title);
    }

    /// @notice 设置 GameRegistry 地址（仅管理员可调用）
    function setGameRegistry(address _gameRegistry) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_gameRegistry != address(0), "Invalid game registry address");
        gameRegistry = _gameRegistry;
    }

    /// @notice 玩家报名参加比赛
    function joinGame() external {
        // 1. 检查状态和时间
        require(status == Types.GameStatus.Created, "Game not accepting players");
        require(block.timestamp < registrationEndTime, "Registration time passed"); // 检查报名时间
        // 2. 检查玩家数量
        require(players.length < maxPlayers, "Max players reached");

        // 3. 检查重复报名
        require(!isJoined[msg.sender], "Already joined");

        // 4. 处理报名费
        if (entryFee > 0) {
            // 从玩家地址转账 entryFee 到本合约作为奖金池的一部分
            require(IERC20(feeToken).transferFrom(msg.sender, address(this), entryFee), "Transfer failed");
        }

        // 5. 更新状态
        players.push(Types.PlayerInfo(msg.sender, 0));
        playerEntryFees[msg.sender] = entryFee;
        isJoined[msg.sender] = true; // 标记玩家已加入

        // 给参与者增加经验（通过 UserLevelManager）
        if (levelManager != address(0)) {
            UserLevelManager(levelManager).addExp(msg.sender, 3 * 10**18); // 3 BLZ = 3 经验
        }

        emit PlayerJoined(msg.sender);
    }

    /// @notice 玩家取消报名并同时拿回报名费
    function cancelRegistration() external {
        address player = msg.sender;

        // 1. 状态检查：必须在报名结束之前
        require(block.timestamp < registrationEndTime, "Registration time is over");
        // 仅允许在 Created 状态下取消报名
        require(status == Types.GameStatus.Created, "Game is already ongoing or ended"); 

        // 2. 检查玩家是否已报名
        uint feeAmount = playerEntryFees[player];
        require(feeAmount > 0, "Player has not joined or fee already refunded");

        // --- 3. 移除玩家数据 (O(N) 搜索 + O(1) 交换并弹出) ---

        // 3a. 找到玩家在 players 数组中的索引
        uint playerIndex;
        bool found = false;
        for (uint i = 0; i < players.length; i++) {
            if (players[i].player == player) {
                playerIndex = i;
                found = true;
                break;
            }
        }
        // 由于 feeAmount > 0，玩家理应在 players 数组中
        require(found, "Internal error: Player not found in list"); 

        // 3b. 交换并弹出：移动数组最后一个元素到当前位置，并移除最后一个元素
        uint lastIndex = players.length - 1;
        if (playerIndex != lastIndex) {
            players[playerIndex] = players[lastIndex];
        }
        players.pop(); // 移除最后一个元素

        // 4. 更新资金状态
        playerEntryFees[player] = 0; // 清零玩家的报名费记录
        prizePool -= feeAmount;      // 从奖池中移除这部分资金
        isJoined[player] = false;
        scores[player] = 0; // 清理玩家成绩记录// 5. 退还报名费 (使用 feeToken)
        require(IERC20(feeToken).transfer(player, feeAmount), "Refund transfer failed");

        // 6. 触发事件
        emit PlayerUnregistered(player, feeAmount);
    }

    /// @notice 比赛创建者/裁判调用此函数开始比赛
    function startGame() external onlyRole(CREATOR_ROLE) {
        require(status == Types.GameStatus.Created, "Game not in Created status");

        // 1. 检查时间是否已过（允许延迟启动）
        require(block.timestamp >= registrationEndTime, "Registration is not over yet"); 
        // 2. 检查最小人数
        require(players.length >= minPlayers, "Not enough players to start"); 

        status = Types.GameStatus.Ongoing;
        emit GameStarted();
    }

    /// @notice 比赛创建者取消比赛，并退还奖池给创建者
    function cancelGame() external onlyRole(CREATOR_ROLE) {
        // 只能在 Created 或 Ongoing 阶段取消
        require(
            status == Types.GameStatus.Created || status == Types.GameStatus.Ongoing,
            "Cannot cancel an ended or distributed game"
        );

        uint refundAmount = prizePool;
        prizePool = 0; // 清空奖池

        // 1. 更新状态
        status = Types.GameStatus.Canceled; // 

        // 2. 将奖池中的所有代币退还给创建者
        if (refundAmount > 0) {
            // 注意：使用 prizeToken 进行转账，因为奖池中存储的是 prizeToken
            require(IERC20(prizeToken).transfer(creator, refundAmount), "Refund failed");
        }

        // 4. 触发事件
        emit GameCanceled(creator, refundAmount);
    }

    /// @notice 玩家提交比赛成绩（支持游戏结果验证）
    function submitScore(uint score) external {
        require(status == Types.GameStatus.Ongoing, "Game not ongoing");
        // 检查比赛是否已开始
        require(block.timestamp >= gameStartTime, "Game has not started yet"); 

        // 1. 检查是否已报名
        require(isJoined[msg.sender], "Player not joined");

        // 2. 检查是否已提交过分数 (此处逻辑可能需要修改：如果允许更新成绩，则移除此检查)
        // require(scores[msg.sender] == 0, "Score already submitted"); 

        scores[msg.sender] = score;

        emit ScoreSubmitted(msg.sender, score);
    }

    /// @notice 玩家提交游戏结果（通过GameRegistry验证）
    /// @param result 游戏结果数据
    function submitGameResult(Types.GameResult calldata result) external {
        require(status == Types.GameStatus.Ongoing, "Game not ongoing");
        // 检查比赛是否已开始
        require(block.timestamp >= gameStartTime, "Game has not started yet"); 

        // 1. 检查是否已报名
        require(isJoined[msg.sender], "Player not joined");

        // 2. 检查游戏类型是否匹配
        require(result.gameType == gameType, "Game type mismatch");
        require(result.player == msg.sender, "Player address mismatch");

        // 3. 调用GameRegistry验证游戏结果
        require(gameRegistry != address(0), "GameRegistry not set");
        GameRegistry registry = GameRegistry(gameRegistry);
        bool verified = registry.verifyGameResult(result);
        require(verified, "Game result verification failed");

        // 4. 更新分数和存储游戏结果
        scores[msg.sender] = result.score;
        gameResults[msg.sender] = result;

        emit ScoreSubmitted(msg.sender, result.score);
    }

    /// @notice 比赛创建者/裁判设置比赛胜者
    /// @dev 支持设置多个胜者（例如，设置前三名），按顺序对应排名 1, 2, 3...
    /// @param _winners 获胜者的地址列表，按排名顺序 (如: [1st_addr, 2nd_addr, 3rd_addr])
    function setWinners(address[] calldata _winners) external onlyRole(CREATOR_ROLE) { // <--- 更新函数名和参数
        require(status == Types.GameStatus.Ongoing, "Game not ongoing");

        // 1. 检查所有胜者是否已报名
        for (uint i = 0; i < _winners.length; i++) {
            require(isJoined[_winners[i]], "There is winner not join");
        }

        // 2. 更新状态
        winners = _winners;
        status = Types.GameStatus.Ended;

        emit WinnersSet(_winners);
    }

    /// @notice 计算并记录奖金到 prizeToClaimsAmount 映射中
    function distributePrize() external onlyRole(CREATOR_ROLE) {
        require(status == Types.GameStatus.Ended, "Game not ended");
        require(winners.length > 0, "No winners set");
        require(prizePool > 0, "Prize pool is empty");

        uint totalPrizePool = prizePool;
        prizePool = 0; // 清空奖池

        UserLevelManager levelMgr = UserLevelManager(levelManager);

        // ... (WinnerTakesAll 逻辑)
        if (distributionType == Types.PrizeDistributionType.WinnerTakesAll) {
            // 赢者通吃：所有奖金给第一名 (winners[0])
            prizeToClaimsAmount[winners[0]] += totalPrizePool;

            // 给第一名额外经验奖励
            if (levelManager != address(0)) {
                levelMgr.addExp(winners[0], 20 * 10**18); // 20 BLZ = 20 经验
            }

        } else if (distributionType == Types.PrizeDistributionType.AverageSplit) {
            // 平均分配：奖池平均分配给所有**参赛者** (players)
            uint playerLength = players.length;
            uint averageAmount = totalPrizePool / playerLength;
            uint remainder = totalPrizePool % playerLength; 

            for (uint i = 0; i < playerLength; i++) {
                uint amount = averageAmount;
                prizeToClaimsAmount[players[i].player] += amount; // 记录可领取金额
            }

            require(IERC20(prizeToken).transfer(creator, remainder), "Refund failed");

        } else if (distributionType == Types.PrizeDistributionType.CustomRanked) {

            // 0. 安全检查：确保获奖人数不超过配置的奖励数量
            require(winners.length <= rankPrizes.length, "Winners list exceeds rank prize configuration");

            uint totalBps = 0;

            // 1. 检查 BPS 总和
            for (uint i = 0; i < winners.length; i++) { 

                uint bps = rankPrizes[i]; 

                require(bps > 0, "Missing prize config for rank");
                totalBps += bps;
            }

            // 此检查确保实际分配的 BPS 没有问题 (initialize 已做过完整检查，此处仅为运行时保险)
            require(totalBps <= 10000, "Prize BPS exceeds 100%"); 

            // 2. 按排名计算并记录奖金
            // 循环 i 从 0 到 winners.length - 1 (代表数组索引 0, 1, 2...)
            for (uint i = 0; i < winners.length; i++) {
                // 索引 i 对应 rankPrizes[i]
                uint bps = rankPrizes[i]; // <-- 修正：使用 i 作为索引
                uint prizeAmount = (totalPrizePool * bps) / 10000;

                prizeToClaimsAmount[winners[i]] += prizeAmount; // 记录可领取金额

                // 给排名前3的玩家额外经验奖励
                if (levelManager != address(0)) {
                    if (i == 0) {
                        levelMgr.addExp(winners[i], 20 * 10**18); // 第一名：20 经验
                    } else if (i == 1) {
                        levelMgr.addExp(winners[i], 10 * 10**18); // 第二名：10 经验
                    } else if (i == 2) {
                        levelMgr.addExp(winners[i], 5 * 10**18); // 第三名：5 经验
                    }
                }
            }
        }

        status = Types.GameStatus.PrizeDistributed;
        // 不再 emit PrizeDistributed 事件，直到实际转账发生
    }

    /// @notice 玩家主动领取奖金
    function claimPrize() external {
        uint amount = prizeToClaimsAmount[msg.sender];
        require(amount > 0, "No prize to claim");

        // 1. 清零奖金记录（重要：必须在转账前清零，防止重入攻击）
        prizeToClaimsAmount[msg.sender] = 0;

        // 2. 执行转账
        // 使用 IERC20.transfer 将代币从合约转给调用者
        require(IERC20(prizeToken).transfer(msg.sender, amount), "Transfer failed");

        // 3. 触发事件
        emit PrizeDistributed(msg.sender, amount);
    }

    /// @notice 玩家主动领取退款
    function claimRefund() external {
        require(status == Types.GameStatus.Canceled, "Game not canceled");
        uint amount = playerEntryFees[msg.sender];
        require(amount > 0, "No prize to claim");

        playerEntryFees[msg.sender] = 0;

        // 2. 执行转账
        // 使用 IERC20.transfer 将代币从合约转给调用者
        require(IERC20(feeToken).transfer(msg.sender, amount), "Transfer failed");

        // 3. 触发事件
        emit FeeRefund(msg.sender, amount);
    }

    /// @dev 内部安全转账辅助函数 (已无用，但如果保留则需要修正)
    // 注意：在拉取模式下，此函数不再被 distributePrize 调用，但如果保留，也应修正。
    function _safeTransferPrize(address recipient, uint amount) internal {
        if (amount > 0) {
            require(IERC20(prizeToken).transfer(recipient, amount), "Transfer failed"); // [cite: 89]
            emit PrizeDistributed(recipient, amount);
        }
    }

    // 添加查看某个玩家成绩的函数
    function getPlayerScore(address player) external view returns (uint) {
        return scores[player];
    }

    function getGameData() external view returns (Types.GameData memory) {
        Types.GameData memory data;
        data.creator = creator;
        data.title = title;
        data.description = description;
        data.status = status;
        data.gameType = gameType; // 新增：包含游戏类型

        data.maxPlayers = maxPlayers;
        data.playerCount = players.length;

        data.registrationEndTime = registrationEndTime;
        data.gameStartTime = gameStartTime;

        data.entryFee = entryFee;
        data.feeToken = feeToken;

        data.prizePool = prizePool;
        data.prizeToken = prizeToken;

        return data;
    }

    /// @notice 获取玩家的游戏结果
    function getPlayerGameResult(address player) external view returns (Types.GameResult memory) {
        return gameResults[player];
    }

}

contracts/GameRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {Types} from "./Types.sol";

/// @title GameRegistry
/// @notice 管理游戏类型注册、验证游戏结果和防止作弊的注册合约
contract GameRegistry is AccessControl {
    bytes32 public constant GAME_ADMIN_ROLE = keccak256("GAME_ADMIN_ROLE");

    // 游戏启用状态映射
    mapping(Types.GameType => bool) public gameEnabled;

    // 防止刷分：记录玩家上次游戏时间
    mapping(address => mapping(Types.GameType => uint256)) public lastPlayedTime;

    // 最小游戏间隔（防止重复提交）
    uint256 public constant MIN_GAME_INTERVAL = 10 seconds;

    // 游戏元数据验证规则
    mapping(Types.GameType => uint256) public maxScores; // 各游戏最大分数限制

    // 事件
    event GameEnabled(Types.GameType gameType);
    event GameDisabled(Types.GameType gameType);
    event GameResultVerified(
        address indexed player,
        Types.GameType gameType,
        uint256 score,
        bytes32 gameHash
    );
    event MaxScoreUpdated(Types.GameType gameType, uint256 maxScore);

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(GAME_ADMIN_ROLE, msg.sender);
        _setRoleAdmin(GAME_ADMIN_ROLE, DEFAULT_ADMIN_ROLE);

        // 启用默认游戏
        gameEnabled[Types.GameType.NumberGuess] = true;
        gameEnabled[Types.GameType.RockPaperScissors] = true;
        gameEnabled[Types.GameType.QuickClick] = true;
        gameEnabled[Types.GameType.InfiniteMatch] = true;

        // 设置各游戏最大分数限制
        maxScores[Types.GameType.NumberGuess] = 100; // 猜数字最高100分
        maxScores[Types.GameType.RockPaperScissors] = 100; // 猜拳最高100分
        maxScores[Types.GameType.QuickClick] = 50; // 30秒内最多点击50次（合理上限）
        maxScores[Types.GameType.InfiniteMatch] = 10000; // 无限消除最高10000分（理论最大值）

        emit GameEnabled(Types.GameType.NumberGuess);
        emit GameEnabled(Types.GameType.RockPaperScissors);
        emit GameEnabled(Types.GameType.QuickClick);
        emit GameEnabled(Types.GameType.InfiniteMatch);
    }

    /// @notice 管理员启用游戏
    function enableGame(Types.GameType gameType) external onlyRole(GAME_ADMIN_ROLE) {
        require(gameType != Types.GameType.None, "Invalid game type");
        gameEnabled[gameType] = true;
        emit GameEnabled(gameType);
    }

    /// @notice 管理员禁用游戏
    function disableGame(Types.GameType gameType) external onlyRole(GAME_ADMIN_ROLE) {
        gameEnabled[gameType] = false;
        emit GameDisabled(gameType);
    }

    /// @notice 更新游戏最大分数限制
    function updateMaxScore(Types.GameType gameType, uint256 newMaxScore)
        external
        onlyRole(GAME_ADMIN_ROLE)
    {
        maxScores[gameType] = newMaxScore;
        emit MaxScoreUpdated(gameType, newMaxScore);
    }

    /// @notice 验证游戏结果（核心防作弊逻辑）
    /// @param result 游戏结果数据
    /// @return bool 验证是否通过
    function verifyGameResult(Types.GameResult calldata result) external returns (bool) {
        // 1. 检查游戏是否启用
        require(gameEnabled[result.gameType], "Game type not enabled");
        require(result.gameType != Types.GameType.None, "Invalid game type");

        // 2. 检查玩家是否在合理间隔内
        uint256 lastTime = lastPlayedTime[result.player][result.gameType];
        require(block.timestamp >= lastTime + MIN_GAME_INTERVAL, "Too frequent submissions");
        lastPlayedTime[result.player][result.gameType] = block.timestamp;

        // 3. 验证分数不超过最大值
        require(result.score <= maxScores[result.gameType], "Score exceeds maximum");

        // 4. 验证时间戳合理（不能是未来时间）
        require(result.timestamp <= block.timestamp, "Invalid timestamp");
        require(result.timestamp >= block.timestamp - 1 days, "Timestamp too old");

        // 5. 验证游戏哈希（确保数据未被篡改）
        bytes32 computedHash = computeGameHash(result);
        require(computedHash == result.gameHash, "Game hash mismatch");

        // 6. 根据游戏类型进行特定验证
        _validateGameSpecificRules(result);

        emit GameResultVerified(result.player, result.gameType, result.score, result.gameHash);

        return true;
    }

    /// @notice 计算游戏数据哈希（用于验证数据完整性）
    function computeGameHash(Types.GameResult calldata result) public pure returns (bytes32) {
        return keccak256(
            abi.encodePacked(
                result.gameType,
                result.player,
                result.score,
                result.timestamp,
                result.metadata
            )
        );
    }

    /// @notice 游戏特定规则验证
    function _validateGameSpecificRules(Types.GameResult calldata result) private pure {
        if (result.gameType == Types.GameType.NumberGuess) {
            // 猜数字游戏验证
            require(result.metadata.length >= 1, "Missing game metadata");
            // metadata[0] 应该是猜测次数（1-5次）
            uint256 attempts = result.metadata[0];
            require(attempts > 0 && attempts <= 5, "Invalid attempt count");
            // 分数应该是 (6 - attempts) * 20
            uint256 expectedScore = (6 - attempts) * 20;
            require(result.score == expectedScore, "Score does not match attempts");

        } else if (result.gameType == Types.GameType.RockPaperScissors) {
            // 石头剪刀布游戏验证
            require(result.metadata.length >= 3, "Missing game metadata");
            // metadata[0] = 玩家胜利次数, metadata[1] = 平局次数, metadata[2] = 失败次数
            uint256 wins = result.metadata[0];
            uint256 draws = result.metadata[1];
            uint256 losses = result.metadata[2];
            require(wins + draws + losses == 10, "Invalid total rounds");
            // 分数应该是 wins * 10 + draws * 5
            uint256 expectedScore = wins * 10 + draws * 5;
            require(result.score == expectedScore, "Score does not match results");

        } else if (result.gameType == Types.GameType.QuickClick) {
            // 快速点击游戏验证
            require(result.metadata.length >= 1, "Missing game metadata");
            // metadata[0] = 实际点击次数
            uint256 clicks = result.metadata[0];
            // 30秒内最多50次点击（每次至少0.6秒）
            require(clicks <= 50, "Too many clicks");
            // 分数应该等于点击次数
            require(result.score == clicks, "Score does not match click count");

        } else if (result.gameType == Types.GameType.InfiniteMatch) {
            // 无限消除游戏验证
            require(result.metadata.length >= 3, "Missing game metadata");
            // metadata[0] = 最终得分, metadata[1] = 到达关卡, metadata[2] = 最大连击
            uint256 finalScore = result.metadata[0];
            uint256 level = result.metadata[1];
            uint256 maxCombo = result.metadata[2];

            // 验证关卡合理性（至少到达第1关）
            require(level >= 1, "Invalid level");
            require(maxCombo >= 1, "Invalid max combo");

            // 验证分数合理性
            // 基础分数计算：每关至少有60个方块（10x6），至少需要30次消除
            // 每次消除基础10分，加上连击加成
            // 所以第1关至少300分
            uint256 minExpectedScore = level * 300;
            require(finalScore >= minExpectedScore, "Score too low for level");

            // 验证分数不超过理论最大值
            // 假设每关平均1000分，最多10关
            uint256 maxExpectedScore = level * 1000;
            require(finalScore <= maxExpectedScore, "Score too high for level");

            // 验证提交的分数与metadata中的分数一致
            require(result.score == finalScore, "Score mismatch with metadata");
        }
    }

    /// @notice 获取游戏信息
    function getGameInfo(Types.GameType gameType)
        external
        view
        returns (
            bool enabled,
            uint256 maxScore
        )
    {
        return (gameEnabled[gameType], maxScores[gameType]);
    }

    /// @notice 检查玩家是否可以进行游戏
    function canPlayerPlay(address player, Types.GameType gameType)
        external
        view
        returns (bool)
    {
        uint256 lastTime = lastPlayedTime[player][gameType];
        return block.timestamp >= lastTime + MIN_GAME_INTERVAL;
    }
}

contracts/Types.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

// import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
// import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";


/// @title Types
/// @notice 包含项目使用的所有共享类型定义
contract Types {

    /// @dev 比赛的生命周期状态
    enum GameStatus {
        Created,            // 比赛已创建，等待报名
        Ongoing,            // 比赛进行中，玩家可提交成绩
        Ended,              // 比赛结束，等待设置胜者和分发奖金
        PrizeDistributed,    // 奖金已分发
        Canceled
    }

    /// @dev 游戏类型枚举
    enum GameType {
        None,               // 无游戏类型（默认值）
        NumberGuess,        // 猜数字游戏
        RockPaperScissors,  // 石头剪刀布
        QuickClick,         // 快速点击
        InfiniteMatch       // 无限消除游戏
    }

    /// @dev 游戏结果结构体（用于链上验证）
    struct GameResult {
        GameType gameType;              // 游戏类型
        address player;                 // 玩家地址
        uint256 score;                  // 游戏得分
        uint256 timestamp;              // 游戏完成时间戳
        bytes32 gameHash;               // 游戏数据哈希（防篡改）
        uint256[] metadata;             // 游戏元数据（具体游戏特定数据）
    }

    /// @dev 玩家信息结构体
    struct PlayerInfo {
        address player; // 玩家地址
        uint score;     // 玩家成绩
    }

    /// @dev 奖金分配方式
    enum PrizeDistributionType {
        WinnerTakesAll, // 胜者全得 (100% 给第一名)
        AverageSplit,   // 平均分配 (奖池平均分配给所有参赛者)
        CustomRanked    // 自定义排名奖励 (使用 prizeDistribution 映射)
    }

    /// @dev 创建新比赛实例时所需配置
    struct GameConfig {
        string title;               // 比赛名称
        string description;         // 比赛描述
        GameType gameType;          // 新增：比赛关联的游戏类型

        address feeTokenAddress;    // 报名费使用的 ERC20 代币地址
        uint entryFee;              // 每位玩家的报名费 (代币数量)

        uint minPlayers;            // 最小玩家数量
        uint maxPlayers;            // 最大玩家数量

        uint registrationEndTime;   // 报名截止时间 (Unix timestamp)
        uint gameStartTime;         // 比赛开始时间 (Unix timestamp)

        address prizeTokenAddress;  // 奖金使用的 ERC20 代币地址
        uint prizePool;             // 奖池

        PrizeDistributionType distributionType; // 奖励分配方式
        // 排名奖励配置 (仅 CustomRanked 使用)
        uint[] rankPrizes; // 索引作为排名，奖励百分比 (基点 BPS: 10000 = 100%)
    }

    /// @dev 比赛的信息
    struct GameData {
        address creator;
        string title;
        string description;
        GameStatus status;
        GameType gameType;              // 新增：比赛关联的游戏类型

        uint maxPlayers;
        uint playerCount;

        uint registrationEndTime;
        uint gameStartTime;

        address feeToken;
        uint entryFee;

        address prizeToken;
        uint prizePool;
    }
}

contracts/UserLevelManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title UserLevelManager
 * @notice 管理用户等级和代币奖励的合约
 * @dev 1 BLZ Token = 1 EXP，等级计算基于累积经验值
 */
contract UserLevelManager is AccessControl {
    address immutable BLZ_TOKEN_ADDRESS;

    // 用户数据结构
    struct UserData {
        uint256 totalExp;       // 总经验值（累积获得的 BLZ 代币数量）
        uint256 currentLevel;   // 当前等级
        uint256 expForNextLevel; // 升级到下一级所需的经验
    }

    mapping(address => UserData) public users;

    // 成就定义
    struct Achievement {
        string name;
        string description;
        uint256 reward; // 奖励的 BLZ 代币数量
        bool isActive;
    }

    mapping(uint256 => Achievement) public achievements;
    mapping(address => mapping(uint256 => bool)) public achievementUnlocked;

    uint256 public constant MAX_LEVEL = 100;
    uint256 public constant BASE_EXP_NEEDED = 100; // 1级升到2级需要的经验
    uint256 public constant EXP_MULTIPLIER = 15;  // 1.5倍递增 (使用 15 表示 1.5，因为 solidity 不支持小数)

    // 事件定义
    event ExpGained(address indexed user, uint256 amount, uint256 totalExp);
    event LevelUp(address indexed user, uint256 newLevel);
    event AchievementUnlocked(address indexed user, uint256 achievementId, uint256 reward);
    event TokensClaimed(address indexed user, uint256 amount);

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant GAME_ROLE = keccak256("GAME_ROLE"); // 游戏合约角色，可以授予奖励

    constructor(address blzTokenAddress) {
        BLZ_TOKEN_ADDRESS = blzTokenAddress;
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(GAME_ROLE, msg.sender);
        _setRoleAdmin(GAME_ROLE, ADMIN_ROLE);

        // 初始化成就
        _initAchievements();
    }

    /**
     * @notice 计算升级到指定等级所需的总经验
     * @param level 目标等级
     * @return 升级所需的总经验
     */
    function getExpForLevel(uint256 level) public pure returns (uint256) {
        if (level == 0) return 0;
        if (level == 1) return 0;

        // 等级经验公式：递归计算，每级所需经验是前一级的 1.5 倍
        uint256 totalExp = 0;
        for (uint256 i = 1; i < level; i++) {
            totalExp += (BASE_EXP_NEEDED * EXP_MULTIPLIER ** (i - 1)) / 10;
        }
        return totalExp;
    }

    /**
     * @notice 获取用户的完整数据
     * @param user 用户地址
     * @return userData 用户数据
     */
    function getUserData(address user) external view returns (UserData memory userData) {
        return users[user];
    }

    /**
     * @notice 增加用户经验（由游戏合约调用）
     * @param user 用户地址
     * @param exp 经验值（BLZ 代币数量）
     */
    function addExp(address user, uint256 exp) external onlyRole(GAME_ROLE) {
        require(exp > 0, "Exp must be positive");
        require(user != address(0), "Invalid user address");

        UserData storage userData = users[user];
        userData.totalExp += exp;

        // 检查是否升级
        uint256 potentialLevel = _calculateLevel(userData.totalExp);
        if (potentialLevel > userData.currentLevel && potentialLevel <= MAX_LEVEL) {
            userData.currentLevel = potentialLevel;
            userData.expForNextLevel = getExpForLevel(potentialLevel + 1) - userData.totalExp;
            emit LevelUp(user, potentialLevel);
        }

        emit ExpGained(user, exp, userData.totalExp);
    }

    /**
     * @notice 解锁成就
     * @param user 用户地址
     * @param achievementId 成就ID
     */
    function unlockAchievement(address user, uint256 achievementId) external onlyRole(GAME_ROLE) {
        require(achievementId > 0 && achievementId <= 7, "Invalid achievement ID");
        require(!achievementUnlocked[user][achievementId], "Achievement already unlocked");

        Achievement storage achievement = achievements[achievementId];
        require(achievement.isActive, "Achievement not active");

        achievementUnlocked[user][achievementId] = true;

        // 发放奖励
        if (achievement.reward > 0) {
            _awardTokens(user, achievement.reward);
        }

        emit AchievementUnlocked(user, achievementId, achievement.reward);
    }

    /**
     * @notice 检查用户是否解锁了指定成就
     */
    function hasAchievement(address user, uint256 achievementId) external view returns (bool) {
        return achievementUnlocked[user][achievementId];
    }

    /**
     * @notice 获取成就信息
     */
    function getAchievement(uint256 achievementId) external view returns (Achievement memory) {
        return achievements[achievementId];
    }

    /**
     * @notice 内部函数：授予用户代币
     */
    function _awardTokens(address user, uint256 amount) private {
        IERC20 token = IERC20(BLZ_TOKEN_ADDRESS);
        require(token.balanceOf(address(this)) >= amount, "Insufficient token balance");

        bool success = token.transfer(user, amount);
        require(success, "Token transfer failed");

        emit TokensClaimed(user, amount);
    }

    /**
     * @notice 根据总经验计算等级
     */
    function _calculateLevel(uint256 totalExp) private pure returns (uint256) {
        if (totalExp == 0) return 1;

        uint256 level = 1;
        uint256 expNeeded = 0;

        while (level < MAX_LEVEL) {
            uint256 nextLevelExp = getExpForLevel(level + 1);
            if (totalExp >= nextLevelExp) {
                level++;
            } else {
                break;
            }
        }

        return level;
    }

    /**
     * @notice 初始化成就系统
     */
    function _initAchievements() private {
        // 游戏类成就
        achievements[1] = Achievement({
            name: "First Win",
            description: "Win your first tournament",
            reward: 20 * 10**18, // 20 BLZ
            isActive: true
        });

        achievements[2] = Achievement({
            name: "Champion",
            description: "Win 10 tournaments",
            reward: 100 * 10**18, // 100 BLZ
            isActive: true
        });

        achievements[3] = Achievement({
            name: "Game Master",
            description: "Participate in 50 tournaments",
            reward: 50 * 10**18, // 50 BLZ
            isActive: true
        });

        achievements[4] = Achievement({
            name: "High Scorer",
            description: "Score above 10000 points in Infinite Match",
            reward: 30 * 10**18, // 30 BLZ
            isActive: true
        });

        // 社交类成就
        achievements[5] = Achievement({
            name: "Social Butterfly",
            description: "Add 10 friends",
            reward: 25 * 10**18, // 25 BLZ
            isActive: true
        });

        achievements[6] = Achievement({
            name: "Popular",
            description: "Receive 50 likes on your profile",
            reward: 40 * 10**18, // 40 BLZ
            isActive: true
        });

        achievements[7] = Achievement({
            name: "Community Leader",
            description: "Create 10 tournaments",
            reward: 60 * 10**18, // 60 BLZ
            isActive: true
        });
    }

    /**
     * @notice 允许管理员添加代币到合约（用于奖励发放）
     */
    function addTokensToPool(uint256 amount) external onlyRole(ADMIN_ROLE) {
        IERC20 token = IERC20(BLZ_TOKEN_ADDRESS);
        bool success = token.transferFrom(msg.sender, address(this), amount);
        require(success, "Token transfer failed");
    }

    /**
     * @notice 获取合约代币余额
     */
    function getTokenBalance() external view returns (uint256) {
        return IERC20(BLZ_TOKEN_ADDRESS).balanceOf(address(this));
    }
}

